import React from 'react'
import { Typography, Stack, Button } from '@mui/material'
import BodyPartImage from '../assets/body-part.png'
import TargetImage from '../assets/target.png'
import EquipmentImage from '../assets/equipment.png'

const Detail = ({ exerciseDetail }) => {
    // const { gifUrl, name, target, equipment } = exerciseDetail;
    const { bodyPart, gifUrl, name, target, equipment } = exerciseDetail;

    const extraDetail = [
        {
            icon: BodyPartImage,
            name: bodyPart
        },
        {
            icon: TargetImage,
            name: target
        },
        {
            icon: EquipmentImage,
            name: equipment
        }
    ]

  return (
    <Stack gap='60px' sx={{flexDirection: { lg: 'row' }, p: '20px', alignItems: 'center'}}
    >
        <img src={gifUrl} loading= 'lazy' className='detail-image' />
        <Stack sx={{ gap: {lg: '35px', xs: '20px'}}}>
            <Typography variant='h3' textTransform='capitalize'>
                {name}
            </Typography>
            <Typography variant='h6' textTransform='capitalize'>
                If you wanna get those {target} yoked, {name}s are the way to go.
                This exercise targets the {bodyPart} muscles and requires a {equipment}
            </Typography>
            {extraDetail.map((item) => (
                <Stack key={item.name} direction='row' gap='24px' alignItems='center'>
                    <Button sx={{ background: 'lightblue', borderRadius: '50%', width: '100px', height: '100px'}}>
                        <img src={item.icon} style={{ width: '50px', height: '50px'}} />
                    </Button>
                    <Typography variant='h5' textTransform='capitalize'>
                        {item.name}
                    </Typography>
                </Stack>
            ))}
        </Stack>
    </Stack>
  )
}

export default Detail
